//import logo from './logo.svg';
import './App.css';
import { Component } from 'react';

import TOC from './conponents/Toc'
//import Subject from './conponents/Subject'
import Content from './conponents/Content'

class App extends Component {

  //생성자
  constructor(props) {
    super(props);
    this.state = {
      mode: 'welcome',
      subject: { title: 'State web', sub: 'world wide web' },
      welcome: { title: 'welcome', des: 'hello, welcome' },
      contents: [
        { id: 1, title: "html", des: "state html" },
        { id: 2, title: "css", des: "state css" },
        { id: 3, title: "javascript", des: "state javascript" }
      ]
    }
  }

  //함수
  render() {
    console.log("App render()");
    var _title, _des = null;
    if (this.state.mode === 'welcome') {
      _title = this.state.welcome.title;
      _des = this.state.welcome.des;
    } else if(this.state.mode === 'read'){
      _title = this.state.contents[0].title;
      _des = this.state.contents[0].des;
    }

    return (
      <div className="App">
        {/* <Subject title ={ this.state.suject.title } sub = { this.state.suject.sub }/> */}

        <header>
          <h1><a href="/" onClick={ (e) => {            
            e.preventDefault();
              this.setState({
                mode: 'read'
              });
            }}>{this.state.subject.title}</a>
          </h1>

          <p>{this.state.subject.sub}</p>
        </header>

        <TOC data={this.state.contents} />
        <Content title={_title} des={_des} />
      </div>
    );
  }
}

export default App;
