import { Component } from 'react';

class UpdateContent extends Component {
    render() {
        console.log("Update Content render()", this.props.data);
        return (
            <article>
                <h2>Update</h2>
                <form action="/create_process" method="post" onSubmit= { (e) => {
                        e.preventDefault();
                        this.props.onSubmit(
                            e.target.title.value,
                            e.target.des.value
                        );                        
                     } }>
                    <p><input type="text" name="title" placeholder="title" value={ this.props.data.title }></input></p>
                    <p><textarea name="des" placeholder="description"></textarea></p>
                    <p><input type="submit"></input></p>
                </form>
            </article>
        );
    }
}

export default UpdateContent;
