import { Component } from 'react';

class UpdateContent extends Component {

    constructor(props){
        super(props);
        this.state = {
            title: this.props.data.title,
            des: this.props.data.des
        }
    }

    render() {
        console.log("Update Content render()", this.props.data);
        return (
            <article>
                <h2>Update</h2>
                <form action="/create_process" method="post" onSubmit= { (e) => {
                        e.preventDefault();
                        this.props.onSubmit(
                            e.target.title.value,
                            e.target.des.value
                        );                        
                     } }>
                    <p><input type="text" name="title" placeholder="title" 
                    value={ this.state.title } onChange = { (e) => {
                        this.setState({ title: e.target.value });
                    } }></input></p>
                    <p><textarea name="des" placeholder="description" onChange = { (e) => {
                        this.setState({ des: e.target.value });
                    } } value = { this.state.des }></textarea></p>
                    <p><input type="submit"></input></p>
                </form>
            </article>
        );
    }
}

export default UpdateContent;
